import React, { useState } from "react";
import LoadingSpinner from "../components/LoadingSpinner";
import Alert from "../components/Alert";

const initialState = {
  stateOfResidence: "",
  firstName: "",
  middleName: "",
  lastName: "",
  mobileNumber: "",
  isMarried: "false",
  streetAddress: "",
  city: "",
  state: "",
  zipCode: "",
  agentFirstName: "",
  agentLastName: "",
  agentCity: "",
  agentState: "",
  agentZipCode: "",
  alternateAgentName: "",
  agentCity: '',
  agentState: '',
  agentZipCode: '',
  alternateAgentName: '',
  realEstate: false,
  personalProperty: false,
  banking: false,
  stocks: false,
  businessOperations: false,
  retirementPlans: false,
  insurance: false,
  estateTrusts: false,
  governmentAssistance: false,
  personalFamilyCare: false,
  makingGifts: false,
  petCare: false,
  effectiveDate: '',
  revokePrior: ''
};

const PowerOfAttorneyForm = () => {
  const [form, setForm] = useState(initialState);
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const validate = () => {
    const newErrors = {};
    if (!form.stateOfResidence) newErrors.stateOfResidence = "State required";
    if (!form.firstName) newErrors.firstName = "First name required";
    if (!form.lastName) newErrors.lastName = "Last name required";
    if (!form.mobileNumber) newErrors.mobileNumber = "Mobile number required";
    if (!form.streetAddress) newErrors.streetAddress = "Street address required";
    if (!form.city) newErrors.city = "City required";
    if (!form.state) newErrors.state = "State required";
    if (!form.zipCode) newErrors.zipCode = "Zip code required";
    if (!form.agentFirstName) newErrors.agentFirstName = "Agent first name required";
    if (!form.agentLastName) newErrors.agentLastName = "Agent last name required";
    if (!form.agentCity) newErrors.agentCity = "Agent city required";
    if (!form.agentState) newErrors.agentState = "Agent state required";
    if (!form.agentZipCode) newErrors.agentZipCode = "Agent zip code required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value
    }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!validate()) return;
    setLoading(true);
    setError("");
    setSuccess("");
    try {
      const { message } = await window.poaAPI.submitPOA(form);
      setSuccess(message || "POA form submitted successfully!");
      setForm(initialState);
    } catch (err) {
      setError(err.message || "Failed to submit POA form");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl w-full space-y-8 bg-white p-8 rounded shadow">
        <h2 className="text-2xl font-bold text-center mb-6">Power of Attorney (POA) Form</h2>
        {error && <Alert type="error" message={error} onClose={() => setError("")} />}
        {success && <Alert type="success" message={success} onClose={() => setSuccess("")} />}
        {loading && <LoadingSpinner size="lg" />}
        <form className="space-y-6" onSubmit={handleSubmit}>
          {/* Section 1: About You */}
          <fieldset className="mb-6">
    if (!form.state) newErrors.state = "State required"; // This line remains unchanged
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">State of Residence</label>
                <select name="stateOfResidence" value={form.stateOfResidence} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500">
                  <option value="">Select State</option>
                  <option value="CA">California</option>
                  <option value="NY">New York</option>
                  <option value="TX">Texas</option>
                  {/* Add more states as needed */}
                </select>
                {errors.stateOfResidence && <span className="text-red-500 text-xs">{errors.stateOfResidence}</span>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Mobile Number</label>
                <input name="mobileNumber" type="tel" value={form.mobileNumber} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.mobileNumber && <span className="text-red-500 text-xs">{errors.mobileNumber}</span>}
              </div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">First Name</label>
                <input name="firstName" value={form.firstName} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.firstName && <span className="text-red-500 text-xs">{errors.firstName}</span>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Middle Name (optional)</label>
                <input name="middleName" value={form.middleName} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Last Name</label>
                <input name="lastName" value={form.lastName} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.lastName && <span className="text-red-500 text-xs">{errors.lastName}</span>}
              </div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Marital Status</label>
                <select name="isMarried" value={form.isMarried} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500">
                  <option value="false">Single</option>
                  <option value="true">Married</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Street Address</label>
                <input name="streetAddress" value={form.streetAddress} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.streetAddress && <span className="text-red-500 text-xs">{errors.streetAddress}</span>}
              </div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">City</label>
                <input name="city" value={form.city} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.city && <span className="text-red-500 text-xs">{errors.city}</span>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">State</label>
                <input name="state" value={form.state} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.state && <span className="text-red-500 text-xs">{errors.state}</span>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Zip Code</label>
                <input name="zipCode" value={form.zipCode} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.zipCode && <span className="text-red-500 text-xs">{errors.zipCode}</span>}
              </div>
            </div>
          </fieldset>
          {/* Section 2: Your Agent */}
          <fieldset className="mb-6">
            <legend className="font-semibold mb-2">Your Agent</legend>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Agent's First Name</label>
                <input name="agentFirstName" value={form.agentFirstName} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.agentFirstName && <span className="text-red-500 text-xs">{errors.agentFirstName}</span>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Agent's Last Name</label>
                <input name="agentLastName" value={form.agentLastName} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.agentLastName && <span className="text-red-500 text-xs">{errors.agentLastName}</span>}
              </div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Agent's City</label>
                <input name="agentCity" value={form.agentCity} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.agentCity && <span className="text-red-500 text-xs">{errors.agentCity}</span>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Agent's State</label>
                <input name="agentState" value={form.agentState} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.agentState && <span className="text-red-500 text-xs">{errors.agentState}</span>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Agent's Zip Code</label>
                <input name="agentZipCode" value={form.agentZipCode} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                {errors.agentZipCode && <span className="text-red-500 text-xs">{errors.agentZipCode}</span>}
              </div>
            </div>
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Alternate Agent Name (optional)</label>
              <input name="alternateAgentName" value={form.alternateAgentName} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
            </div>
          </fieldset>
          {/* Section 3: Powers */}
          <fieldset className="mb-6">
            <legend className="font-semibold mb-2">Powers</legend>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {[
                "RealEstateMatters",
                "PersonalPropertyMatters",
                "BankingTransactions",
                "StockBondTransactions",
                "BusinessOperatingTransactions",
                "RetirementPlans",
                "InsuranceAnnuities",
                "EstateTrustMatters",
                "GovernmentAssistance",
                "PersonalFamilyCare",
                "MakingGifts",
                "PetAnimalCare"
              ].map((power) => (
                <label key={power} className="block text-sm font-medium text-gray-700">
                  <input type="checkbox" name={power} checked={form[power]} onChange={handleChange} className="mr-2" />
                  {power.replace(/([A-Z])/g, ' $1').trim()}
                </label>
              ))}
            </div>
          </fieldset>
          {/* Section 4: Details */}
          <fieldset className="mb-6">
            <legend className="font-semibold mb-2">Details</legend>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">When powers become effective</label>
                <select name="effectiveWhen" value={form.effectiveWhen} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500">
                  <option value="immediately">Immediately</option>
                  <option value="incapacitated">If I become incapacitated</option>
                </select>
              </div>
              <div className="flex items-center mt-6">
                <input type="checkbox" name="revokePriorPOA" checked={form.revokePriorPOA} onChange={handleChange} className="mr-2" />
                <span className="text-sm text-gray-700">Revoke prior powers of attorney</span>
              </div>
            </div>
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Additional Instructions</label>
              <textarea name="additionalInstructions" value={form.additionalInstructions} onChange={handleChange} className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
            </div>
          </fieldset>
          <button type="submit" className="w-full bg-blue-600 text-white px-4 py-2 rounded font-semibold">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default PowerOfAttorneyForm;
